import { useState, useEffect } from "react"

export const Notes = () => {
    const [absence, setAbsence] = useState(0)
    const [endurance, setEndurance] = useState(0)
    const [noteGlobale, setNoteGlobale] = useState(0)

    useEffect(()=>{
        setNoteGlobale((absence+endurance)/2)
    }, [absence, endurance])

    const augmentationAbsence = () => {
        setAbsence(absence+1)
    }
    

    const reductionAbsence = () => {
        setAbsence(absence-1)
    }

    return(
        <div>
            <span>Absence</span> <button onClick={reductionAbsence}>-</button> {absence} <button onClick={augmentationAbsence}>+</button><br></br>
        </div>
    )
}
